INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10037, 1, "Ad-hoc mimarlık ", " Modern mimarlığa karşı 1970’lerin başında doğmuş olan akım. Parçaların bir amaca yönelik olarak ya da bir sorunu çözmek üzere bir araya getirilmesini savunur. Ad-hoc’çuluk, ilk kez Charles Jencks’in <em>“Architecture 2000”</em> adlı kitabında bir akım olarak belirtilmiş, Peter Cook’un Ad-hoc konut değişimi projesiyle somutlaşmıştır.", "(ad hok okunur)", NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
