INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10049, 1, "agrega", " Harç, beton ya da asfalt gibi malzemenin ana bölümünü meydana getiren, kırmataş, çakıl, kum, cüruf gibi âtıl gereçler. Kum ve çakıl, daha büyük taşların kırılmasıyla da elde edilebilir. Uygun nitelik ve karışımda yüksek fırın cürufu; lav cürufu, süngertaşının ufalanmasından elde edilmiş gereçler de yerine göre agrega olarak kullanılabilir. ﬁist ve şistle karışık, parçalanabilir taşlar agrega olarak kullanılmaya elverişli değildir. Agrega içinde pislik, şist, kil, mil, mika, organik maddeler ve çinko sülfür bulunmamalıdır.", "Fr. <strong>(agrégat)</strong>", NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
