INSERT INTO entry_usages (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10052, NULL, "ağacın nemi", " Ağaçta bulunan nem miktarının, ağacın kuru olduğu haldeki ağırlığına oranı. Yeni kesilmiş bir ağaçta bu oran çoğu kez %100’ün üzerindedir. Ağaç, kullanılacağı zaman bu oran dülgerlikte %22’den, marangozlukta %17’den az olmalıdır. Bkz. nem katsayısı.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
