INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10125, 1, "aksonometri, aksonometrik izdüşüm", " Bir cismi (örneğin bir yapıyı) üç boyutlu olarak gösteren bir geometrik çizim türü; paralel izdüşümle elde edilen perspektif, <em>paralel izdüşüm, paralel perspektif. </em>Aksonometri <strong>dik aksonometri</strong> ve <strong>eğik aksonometri</strong> olarak iki gruba ayrılır. Aksonometride göz sonsuzdadır. Aksonometrik izdüşüm için cismin planı bir doğru üzerine uygun bir açıyla yerleştirilir. Planın köşe noktalarından ölçekli olarak dikmeler çıkılır ve dikme uçlarından plana paralel çizgiler çizilir. Sonuçta, elde edilen resimde yatay düzlemdeki boyutlar ve düşey doğrular ölçekli olurlar, buna karşılık, düşey düzlemlerdeki köşegenler ve eğriler bozulur. Bir aksonometrik izdüşüm, eksenler arasındaki üç açının eşit olması (120°) durumunda <strong>izometrik,</strong> yalnızca iki açının eşit olması durumunda <strong>dimetrik,</strong> üç açının da farklı olması durumunda <strong>trimetrik</strong>tir. Bkz. izdüşüm, izometrik izdüşüm.", "Yun. (<strong>axon</strong> eksen, <strong>metron</strong> ölçü)", NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10126, 1, "aksonometrik", "Aksonometri yöntemleriyle yapılmış (çizim): aksonometrik perspektif.", " sıf. ", NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
