INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10129, NULL, "mimari akustik", " Binalarda, sesin iyi işitilebilmesi için mimarların ve mühendislerin kullandıkları tekniklerin bütünü. Mimari akustiğin işlevi, istenmeyen sesleri en aza indirmek, istenen sesleri de dinleyicilere, bozulmadan ve gereken şiddette iletmektir.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
