INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10131, 1, "	akustik yalıtım", " Akustik yalıtım, gürültünün kaynağına göre yapılır. Havadan gelen gürültü, bir duvarı ya da döşemeyi geçerken bu bileşenin birim yüzey kütlesinin büyüklüğü oranında zayıflar. Bu bakımdan, geçirimsizlik sağlanmak istenen yerlerin döşeme ve duvarlarının yoğun malzemeden yapılması yararlıdır. Titreşimler ve çarpma sesleri iletim yoluyla malzemeye yayılır. Bu sesleri kaynaklarında yalıtmak gerekir. Örneğin, halı ayak seslerini keser.", "ses yalıtımı, ses tecridi, ses izolasyonu.", NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
