INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10146, 1, "albastra, albastron", " İçine güzel kokular konan, armut biçimli Eski Yunan ya da Roma testisi. Bu küçük testiler çoğunlukla, albatr denilen yumuşak, beyaz mermerden yapıldıklarından bu adı almışlardır. Bunların camdan, pişmiş topraktan, madenden yapılmış olanları da vardır.", "Yun.", NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
