INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10150, 1, "alçı", " Alçıtaşını özel fırınlarda 120-180°C’de ısıtıp bağlı bulunduğu kristal suyunu uçurarak ve kalan kalsiyum sülfatı (CaSO<sub>4</sub>) toz haline getirirek elde edilen gereç. Alçı su ile karılınca katılaşma ve sertleşme özelliği olduğundan yapılarda ve heykelcilikte kullanılmaktadır. Alçı sertleşince, yitirdiği sudan dolayı içinde boşluklar oluşur. Bu boşlukların çokluğu alçının dayanımını azalttığından su/alçı oranı çok önemlidir. Bu oranla, dayanım ters orantılıdır. Alçı sertleşirken bir miktar hacim artması meydana gelir. Bu özelliğinden dolayı alçı, kalıp almakta kullanılır. Alçı yangına karşı koruyucu bir maddedir. İçinde 2 molekül su bulunduğundan ısının büyük bir bölümü bu suyun ayrılmasına gider. Bu ayrılan su, buhar tabakası halinde alçının içine dolarak yangının yayılmasını önler. Alçı kimyasal yapısına ve yapıda kullanılış şekline göre <strong>stük alçısı, sıva alçısı, döşeme alçısı</strong> gibi adlar alır. Yapıda kullanılan alçı, alçıtaşının pişirilip öğütülmesiyle elde edilen alçıdır. Alçıda, gerektiğinden az ya da çok pişmiş kısımlar, kil, kireç vb. yabancı maddeler bulunmamalıdır. Su emme yeteneği, ağırlık olarak %70’i geçmemelidir. Alçı, çimentoya ağırlığının %1-2’si oranında katılırsa katılaşmayı yavaşlatır, %3’ü oranında katılırsa katılaşmayı hızlandırır.", NULL, NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10150, NULL, "adi alçı, stük alçısı", " Alçıtaşının, bağlı olan kristal suyunun kısmen uçurulmasıyla elde edilen yarım hidratlı kalsiyum sülfattan ibaret alçı (CaSO<sub>4</sub>, 1/2 H<sub>2</sub>O).", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10150, NULL, "anhidrit alçı", " Çok fazla inceltilen susuz kalsiyum sülfat (CaSO<sub>4</sub>). Bileşimi %41,2 kireç (CaO), %58,8 sülfat asidi anhidritinden (SO<sub>3</sub>) ibarettir. İç sıva ve rabiç sıva yapımında kullanılır; dışta kullanılmaz. Anhidrit alçı suyun içine serpilerek ve olabildiğince az su ile karıştırılarak kullanılır.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10150, NULL, "döşeme alçısı", " (CaSO<sub>4</sub>) Çok geç sertleşen, fakat yüksek dayanım kazanan alçı; <em>mermer alçısı, yanmış alçı, ölü alçı. </em>Bu alçı döşeme kaplaması olarak da kulanılabilir. Bazı maddeler katılarak pişirilmesiyle, iyi cila tutan, renkli döşeme kaplama taşları elde edilir.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10150, NULL, "gözenekli alçı", " Alçının karma suyuna hidrojen peroksit ya da kalsiyum klorür katılarak elde edilen alçı. Gözenekli alçı, ısıyı ve sesi tutan yapı bileşenleri hazırlamakta kullanılır.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10150, NULL, "ince inşaat alçısı", " İçinde büyük taneler bulunmayan, son kat sıvada kullanılan alçı.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10150, NULL, "kaba inşaat alçısı", " İlk kat olarak kullanılan kaba öğütülmüş alçı.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10150, NULL, "kalıp alçısı", " Porselen, fayans yapımında ya da başka şeylerin kalıbını almakta kullanılan alçı.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10150, NULL, "katkılı alçı", " Adi alçının işlenme özelliklerini daha elverişli hale getirmek için priz (I) geciktirici madde (sodyum kazeinat vb.) katılması yoluyla elde edilen alçı. Katkılı alçı; ince sıva, kartonpiyer, kalıp yapma, rabiç işlerinde ve süslemelerde kullanılır.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
