INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10150, NULL, "Paris alçısı", " Süsleme öğelerinin dökümünde kullanılan yarı hidratlı alçı.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
