INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10150, NULL, "sıva alçısı", " Saf kalsiyum anhidrit ve stük alçısı karışımı olup piyasada satılan ve bundan dolayı piyasa alçısı adını da alan alçı (CaSO<sub>4</sub> + CaSO<sub>4</sub> , 1/2 H<sub>2</sub>O.) Bu alçı suya dayanıklı olmadığından dışta ya da yapıların ıslak bölümlerinde kullanılmaz. ", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
