INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10150, NULL, "şaplı alçı ", "150° C’de pişirilerek bir şap eriyiğine batırılan alçıtaşlarının 600-700°C’de pişirildikten sonra öğütülmesiyle elde edilen alçı.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10150, NULL, "yüksek dirençli alçı", " İçinde, neme doymuş havada düşük sıcaklık ile hazırlanmış yarı hidrat ve sülfat anhidrit türleri bulunan alçı.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
