INSERT INTO entry_usages (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10150, NULL, "alçı levha ", "Bölücü duvar, asma tavan ya da sıva yerine kaplama yapmakta kullanılan, yapı alçısı ile lifli bir maddeden yapılmış, iki yüzü genellikle karton kaplı levha. Islak hacimler ve yangına dayanıklılık gerektiren durumlar için özel üretilmiş çeşitleri vardır.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
