INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10153, 1, "alçıtaşı,-nı", " Doğada genellikle jips, anhidrit ve selenit olarak rastlanan ve Ca<sub>++</sub>, SO<sub>4</sub><sub>– –</sub> iyonlarının bileşiminden oluşan ve oluşum ortamına göre içeriğinde kimi zaman su bulunan mineral topluluğuna piyasada mineral farklılığına bakılmaksızın verilen ad. Yapı alçısı jipsten, kimi özel alçılar ise anhidrit kayasından elde edildikleri halde, alçıtaşı denildiğinde genellikle jips anlaşılır.", NULL, NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
