INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10157, 1, "Aleksandrin üslubu, Aleksandr dönemi üslubu", "Rusya’daki Ampir üslubu. Rusya’da St. Petersburg’da I. Alexandre (1801-25) ve I. Nicolas (1825-55) dönemlerinde görülen, ve 1800-30 arasında Paris’ten bütün Avrupa’ya yayılan Ampir üslubundan kaynaklanan tarz. ", NULL, NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10158, 1, "alem", " Minare, kubbe, sancak direği gibi yüksek şeylerin tepesine yerleştirilen madenden yapılmış hilal, ay yıldız ya da lale şeklinde süslü tepelik. Alemlerin çeşitli bölümlerine <strong>hilâl, küçük küp, boyun, armut, bilezik, büyük küp, kova</strong> gibi adlar verilir. Alemler genellikle bakırdan içi boş olarak yapılırlar ve üzerlerine altın yaldız sürülür. Kubbe mimarlığından önce, Türklerde çadır tepesine de alem yerleştirilir ve bunlara <strong>moncuk</strong> denirdi. Alemler estetik kaygının yanısıra, yapısal bir zorunluluğun da sonucu olup kurşun levhaların tepedeki birleşme noktasını örterler.", "Ar.", NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_usages (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10158, NULL, "alem bileziği", " Bir alemde büyük küp ile armut arasındaki kalın halka.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10159, 1, "alemci", " Kubbelere, minarelere alem yapan ve takan kimse.", NULL, NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
