INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10161, 1, "algılayıcı", " Bir otomatik ayarlama ya da kumanda sisteminde fizik büyüklüklerdeki değişmeleri (basınç, ısı, rüzgâr hızı, işleme bozuklukları gibi) algılayarak elektrik ya da elektronik mesaja dönüştüren düzenek. Algılayıcılar algıladıklarını çoğu kez uygulayıcılara aktarırlar.", NULL, NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10162, 1, "alın duvarı", " Bir beşikçatıda damlalık aşığının yanında giden duvar parçası. Alın duvarı merteklere dik doğrultudadır. Bkz. çatı, kalkan duvarı, kargalık.", NULL, NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10163, 1, "alın tahtası", " Bir ahşap saçakta merteklerin uçlarını birleştiren, yani saçağın alnına boydan boya yerleştirilen tahta.", NULL, NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
