INSERT INTO entry_definitions (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10165, NULL, NULL, "Kimi yapılarda giriş kısmının ya da kapı ve pencerelerin üstünde bulunan üçgen veya yarı değirmi süsleme, <em>°fronton.</em> Alınlıklar biçimlerine göre <strong>basık alınlık, düz tepeli alınlık, çifte alınlık, eteksiz alınlık, eğrili alınlık, halkalı alınlık, kesik alınlık, kırma alınlık, kıvrımlı alınlık, üçgen alınlık, yuvarlak alınlık</strong> gibi adlar alırlar. Etrüsk tapınaklarında alınlık çıkıntı halindedir. Yunan ve Roma mimarlıklarında, üçgen şeklinde olup saçaklığın üzerine oturur. Roma’da alınlığın tepesindeki açı oldukça dardır ve çatının eğimine bağlı değildir. Ortaçağ’da alınlığın yerini kalkan duvarı ve pencere tepeliği almış, Rönesans’ta yeniden alınlığa dönülmüştür. Alınlık 16.yy.’da Avrupa’da mobilyalara da uygulanmış, daha sonra seçmeci (eklektik) yapılarda yeniden görülmüştür. Modern mimarlıkta kaybolmuşsa da Postmodern akımla yeniden gündeme gelmiştir. ", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_definitions (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10165, NULL, NULL, "Bir koltuk ya da kanepede oturma yeri ile dirseklik arasındaki boşluğu dolduran kumaş bölüm.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
