INSERT INTO entry_usages (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10171, NULL, "alkali dayanıklılığı", " Banyo, çamaşırlık gibi yerlerde veya taze beton, tuğla ya da sıva üzerinde kullanılan boyalar ve sıvı kaplama gereçlerinin kireç, çimento ve sabunlara yani alkalik etkilere karşı gösterdikleri dayanıklılık. Sentetik reçinelerin çoğunun bu özelikleri vardır. Boyarmaddeler de bu bakımdan çok dikkatli seçilmelidirler. Prusya mavisi ve alüminyum tozu alkalik etkisi olan yerlerde kullanılmazlar.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
