INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10190, 1, "altınbölüm, altınkesim, altınoran", " Eski Yunanların tapınaklarına uyguladıkları ve o çağdan beri iki uzunluk arasında en güzel ilişkiyi verdiği benimsenen aritmetik oran. Bir C doğrusu A ve B gibi, eşit olmayan iki parçaya bölündüğünde A/B=B/C oranının sağlanması. Rönesans’ta üzerinde çok durulan altınbölümün günümüz tasarımlarında geçerliliği kalmamıştır. Bkz. altınsayı, oran.", NULL, NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10191, 1, "Altınoluk", " Kâbe’nin damındaki suları toplamak için yapılmış altın kaplı oluk. Bu oluk ilk kez Osmanlı padişahı Kanuni Sultan Süleyman zamanında yaptırılarak Kâbe’ye gönderilmiştir. Geleneğe göre yeni altınoluk her yıl emirülhac tarafından Kâbe’ye götürülür, eskisi geri getirilirdi. Altınoluk, büyük camilerden birinin iç harem avlusunda hazırlanırdı.", NULL, NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
