INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10193, 1, "altınsayı", " İnsan vücudu ile birçok bitkide bulunan oranları gösteren ve altınoranın da sonucu olan sayı, <p>1+√5</p><p>2 </p><p> (Yaklaşık 5/3 oranı)</p><p>Yunan matematikçisi Pythagoras bunu, düzgün bir beşgen içine çizilmiş beş köşeli yıldızla şöyle ifade etmiştir (Bkz. çizim s.33).</p><p>AC = AB x 1,618 </p><p>AC / AB = 1,618</p>", NULL, NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
