INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10199, NULL, "sosyal altyapı", " Sağlıklı bir çevre meydana getirmek amacıyla yapılması gereken eğitim, sağlık, dinsel, kültürel ve yönetsel yapılar ile park, çocuk bahçeleri, açık spor alanları gibi yeşil alanlara verilen genel ad.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
