INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10201, 1, "alümin", " Alüminyum oksidinden ibaret olan bir taş, <br>(Al<sub>2</sub>O<sub>3</sub>); kimyasal bileşim olarak kilin ana maddesi. Alümin, içinde bulunan ve değişik renkler almasını sağlayan öteki oksitlerle yakut (kırmızı), zebercet (sarı), safir (mavi) gibi birçok değerli taşı oluşturur. Hidratlı alümin doğada bulunur ve boksit adını alır.", "Fr. <strong>(alumine)</strong><strong> </strong>&lt; Lat. (<strong>alumen</strong> şap) kim.", NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10202, 1, "alüminat", " Alüminin asit anhidriti rolünü oynadığı tuz. Kalsiyum alüminatlar erimiş alüminli çimentoların ana maddesidir. Renkli alüminatlar seramiklerde boyarmadde olarak kullanılır. Kobalt alüminatlar mavi, krom alüminatlar yeşil ya da pembedir.", "Fr. (<strong>aluminate</strong>) kim.", NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
