INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10204, 1, "alüminyum", NULL, "Fr. <strong>(aluminium)</strong>", NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_definitions (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10204, NULL, NULL, "Bu yüzden, taşıyıcı olarak kullanılacağı durumlarda, başka madenlerle karıştırılarak hafif alaşımlar adı verilen alaşımlar haline getirilir. İndirgenmesi, boksitin elektrolizi ile olmaktadır. Alüminyumun yapıda kullanılmasının başlıca yararları, hafifliği (kullanılan normal malzemeden üç kez daha hafif), sağlamlığı ve çeşitli biçimlere kolaylıkla sokulabilmesidir. Alüminyum genellikle iskelette, giydirme cephelerde, doğrama işlerinde, tenekecilik işlerinde ve çeşitli eşyada kullanılmaktadır. Mimarlıkta, alüminyum önceleri örtü malzemesi olarak kullanılmış, cephe kaplaması olarak ilk kez 1937 yılında Fransız Jean Prouvé tarafından Clichy’de halkevi binasında uygulanmıştır. Pittsburg’da 1952 yılında Harrison ve Abromowitz tarafından inşa edilmiş olan Alcoa (Aluminum Company of America) binası alüminyumun mimarlıktaki iddiasını ortaya koyar. 33 katlı olan bu bina, pencereleri de içeren ve yalnızca on kişi ile bir ayda monte edilen 2500 prefabrike alüminyum pano ile kaplanmıştır. Mimarlıkta alüminyumun bir başka ilginç örneği de Paris’te P. Dufau’nun yaptığı, 1110 alüminyum panodan meydana gelen, 61 m çapındaki bir kayzer kubbesiyle örtülen spor sarayıdır. Bkz. anotlama, eloksal.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10204, NULL, "dövme alüminyum alaşımı", " Soğuk çekme, dövme, presleme, çekme yoluyla biçimlendirilen hafif alaşım.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
