INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10211, 1, "ameliyathane", " Hastanelerde hastalara ameliyat yapılan oda. Yeni ameliyathanelerde pencere yapılmadığı gibi tepeden doğal ışık da alınmamaktadır. Aydınlatma yapay olarak sağlanırken, binanın ana sisteminden bağımsız, hijyenik bir iklimlendirme düzeni kurulur. Septik ve aseptik ameliyathaneler, düzenlenmeleri ve boyutları bakımından farksızdır. Normal ameliyathanelerde alan yaklaşık 6x6 m, yüseklik anestezi maddelerinin buharlaşmasından ötürü 4-5,5 m’dir. Temizlik kolaylığı sağlamak üzere ameliyathanelerde kolay temizlenebilir yapı malzemeleri kullanılması, daraçılı köşelerden kaçınılması, hattâ ayrıtlardaki malzemenin yuvarlatılması gerekir.", "Ar. Far.", NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
