INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10220, 1, "Ampir, Ampir üslubu", "I. Napolyon döneminde Paris’te başlamış ve 1800-40 arasında bütün Avrupa’ya hattâ Türkiye’ye yayılmış olan mimarlık, mobilya, giyim vb. tarzı;<em> İmparatorluk üslubu.</em> Fransız İhtilali’nden sonra, edinilen bilgilerin ve özellikle de ressam David’in etkisi ile Antikçağ tarz ve üsluplarının yorumlanmasına başlanmıştı. XVI. Louis döneminin sonlarına doğru zaten sadelik kazanmaya başlamış olan dekoratif sanatlar İhtilal sırasında daha ciddi ve daha süssüz silmeler ve çevre çizgileri ile belirlendi. Duvarlar fresklerle süslenirken mobilyalar Antikçağ biçimlerini ve süslemelerini aldılar. Yaldızlı bronzla süslü akaju ağacının dile getirdiği bu “sözde antik”lik <br>I. Napolyon tarafından benimsenerek imparatorluğun üslubunu oluşturdu. I. Napolyon dönemi tarzına sonradan <strong>Birinci Ampir üslubu</strong>, III. Napolyon dönemindekine ise <strong>İkinci Ampir üslubu</strong> adı verildi. Ampir üslubu, katılığına karşın görkemden yoksun değildir. Sütunların, kaide ve başlıklarında bazen yaldızlı bronzdan, bindirilmiş yumurta ya da defne şeklinde bir gerdanlık bulunur, gövde yivsizdir. Ampir mobilyası çokluk kübik ve masiftir; benimsenen tek eğri, dairedir. Rusya’ya da yayılan Ampir üslubu, orada <strong>Aleksandrin üslubu</strong> adıyla anılmıştır. Avrupa’da Barok’tan sonra ortaya çıkan Ampir Üslubu bizde Barok’la aynı zamanda görülür.", "Fr. <strong>(empire)  </strong>", NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10220, NULL, "İkinci Ampir Üslubu", NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10220, NULL, "Türk Ampir Üslubu", " 1845-74 arasında, özellikle İstanbul’da yaygın olarak görülmüştür. Fransa’dan gelen kimi sanat eşyası ve oradaki yapıları inceleme olanağını bulan Hıristiyan mimarlar yoluyla Türkiye’de yeni bir akım olarak beliren Ampir Üslubu, Avrupa’daki uygulamadan büyük farklılıklar göstermiştir. Bu nedenle de Türkiye’deki uygulamaya <strong>Türk Ampir Üslubu</strong> denmiştir. İnsan ve hayvan figürlerinin zengin bir şekilde ve çeşitli biçimlerde kullanılmasına ve bunların bitkisel motiflerle bağlanmasına dayanan bu üslup, yalnız mimarlıkta kalmamış, resim, heykel, süsleme, seramik ve mobilya gibi çeşitli sanat kollarında da denenmiştir. İnsan ve hayvan figürlerinin ağır bastığı böyle bir üslubun aktarılışı sırasında dinî gelenekler göz önüne alınarak insan ve hayvan figürlerinden olabildiğince kaçınılmış, daha çok çiçek ve yaprak gibi bitkisel süslemeler yeğlenmiştir. En önemli örneklerin toplandığı İstanbul’da özellikle, Paris’te yetişmiş mimar Kirkor Balyan tarafından Tophane’de yapılmış olan Nusretiye Camisi, Barok’tan Ampir’e geçişi gösterir. Karakteri dolayısıyla Barok sayılamayacak olan Nusretiye Camisi tam bir Ampir üslubu örneği olmamakla birlikte kimi öğeleri bakımından Türk Ampiri sayılmaktadır. 1853’te Hacı Emin Paşa ve Sarkis Balyan tarafından yapılan Dolmabahçe Sarayı ve Ortaköy Camisi (1854) Barok ve Ampir üsluplarının karışımından meydana gelen örneklerdir. Ayrıca II. Mahmut Türbesi (1840), Sultanahmet’teki Cevri Kalfa okulu, Topkapı Sarayı içindeki kimi binalar, Alay Köşkü (1810) ve Kuleli, Rami, Maçka, Taksim kışlaları ile Taşkışla bu dönemin yapıtları arasına girerler. Mimarlığın yanısıra çeşitli süsleme dallarında da bu dönemin karakteristik belirtileri ahşap evlerin kapı, pencere, dolap, tavan süslemelerinde ve mezar taşlarında görülmektedir.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10221, 1, "ampul,-lü", " İçinde elektrik akımını ışığa dönüştüren bir düzeneği bulunan saydam ya da yarı saydam bir cam tüp ya da şişe, <em>lamba</em> (I).", "Fr. <strong>(ampoule)</strong>", NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
