INSERT INTO entry_definitions (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10243, NULL, NULL, "Eski Romalılarda, içinde halk eğlencelerinin düzenlendiği, yuvarlak ya da oval planlı, ortasında arena adı verilen gösteri alanı, onun çevresinde seyirciler için kademeli oturma yerleri bulunan, dışı yüksek duvarlı yapı (örnek: Roma’daki Colosseum). Anfitiyatro yuvarlak ya da oval biçimiyle, iki ahşap tiyatronun birleşmesinden ortaya çıkmıştır. Bkz. tiyatro. <br>Bir tiyatroda yarım daire şeklinde galeri.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
