INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10245, 1, "Anglosakson mimarlığı", " İngiltere’de 6.yy.’ın sonundan Norman istilasına (1066) kadar olan dönemdeki mimarlık; Romanesk öncesi İngiliz mimarlığı. <strong>Sakson mimarlığı</strong> da denir. Tarz, ağır duvarlar ve yuvarlak kemerlerle belirginleşir.", NULL, NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
