INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10249, 1, "Anıtkabir", " Atatürk için Ankara’da yapılmış olan anıtsal kabir. Anıtkabir için 1 Mart 1941’de açılan ve 2 Mart 1942’de sona eren uluslararası yarışmaya çeşitli ülkelerden toplam 49 proje katıldı. Prof. Bonatz’ın başkanlığını yaptığı seçici kurul sonuçta üç projeyi, Türk mimarları Emin Onat ile Orhan Arda’nın, Alman Profesör Johannes Kruger’in ve İtalyan Profesör Arnaldo Foschini’nin projelerini birinci seçti. Hükümet bunlardan Emin Onat ile Orhan Arda’nın projesinin uygulanmasına karar verdi. Bu projeye göre 9 Ekim 1944’te Rasattepe’de (bugünkü Anıttepe) temeli atılan ve 1953 yılında tamamlanan yapı, İkinci Ulusalcı Mimarlık örnekleri arasındadır.", NULL, NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
