INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10264, 1, "antefiks", " Eskiden kiremitlerin uçlarını tutmak üzere yapıların çatılarına ya da frizlerine konulan çoğu pişmiş topraktan yapılmış süslere verilen ad, <em>kiremit ağızlığı.</em> Saçak uçlarına yerleştirilen antefikslerden kimi zaman çörten olarak da yararlanılmıştır. Antefikslerin bir başka türü de iç ve dış frizlerin süslenmesinde kullanılan küçük, alçak kabartmalardır.", "Lat. <strong>(antefixa)</strong>", NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
