INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10298, 1, "apsit", " Kiliselerde koronun arkasında bulunan ve camilerin mihrap kısmının karşılığı olan, yarım daire ya da yarım çokgen şeklinde çoğu fırıntonozu ile örtülü bölüm. Apsitler çoğu zaman apsidiyollerle çevrili olurlar. Romalılar yapı dışına taşan yarım daire şeklindeki gözlere <strong>absida</strong> derlerdi. Bazilikaların uçlarında bir absida bulunurdu. Kiliselerdeki apsitlerin kökeni budur. ", "Yun. (<strong>apsis</strong>, <strong>idos</strong> daire, tonoz)", NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10299, 1, "apter, apteros", " Eski Yunan ve Roma sanatında, geleneklere göre kanatlı olan tanrı figürlerinin, bazen kanatsız ya da kesik kanatlı yapılmış heykelleri için kullanılan terim. Bu heykellerin en ünlü örneği, Atina kentinin zafer tanrıçasını temsil eden Nike Apteri’dir.", "Yun. (<strong>a </strong>“sız”, <strong>pteron</strong> kanat)", NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_usages (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10299, NULL, NULL, " Eski Yunan ve Roma mimarlığında, ‘kanatsız’ yani iki uzun kenarında sütun bulunmayan tapınak türü. Apter tapınaklar, ön ve arka cephelerinin özelliklerine göre üç ayrı alt tip içinde kümelendirilir: <strong>in antis, prostil, anfiprostil. </strong>", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
