INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10314, 1, "aramid elyaf", "Isıya dayanıklı ve çok yüksek çekme dayanımlı bir sentetik elyaf. Daha çok uzay endüstrisi ve askerî uygulamalarda kullanılmaktadır. Doğal kimyasallara karşı dirençlidir; asit ve alkalilerden etkilenir. Kevlar ve Nomex, aramid elyafın ticari örnekleridir.", "(aromatic polyamid’in kısaltılmışı) ", NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
