INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10358, 1, "armonik oranlar", " Mimarlığı müziğe bağlayan bir oranlar dizgesi (sistemi). Eski dönemlerde, yaya gerilen iki ipten birinin uzunluğunun ötekinin iki katı olması halinde, atışlardaki farkın bir oktav; 2/3’ü olması halinde beşte bir; 3/4’ü olması halinde dörtte bir oktav olduğu bulunmuştu. Böylece mimarlıkta, oranları 1:2, 2:3 ya da 3:4 olan odaların ya da bütün binaların uyumlu olacağı ilkesi benimsendi. Erken Rönesans mimarları, özellikle de Alberti, bu buluşu Roma mimarlığının güzelliğinin ve evrenin ahenginin anahtarı olarak kabul ettiler. Bu düşünce, daha sonraları Venedikli müzik kuramcılarının da yardımları ile majör ve minörlere dayanan çok karmaşık bir oranlar skalası meydana getiren Palladio tarafından geliştirildi.", NULL, NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
