INSERT INTO entry_usages (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10370, NULL, "Arsa Ofisi ", "1969 yılında 1164 sayılı yasa ile İmar ve İskân Bakanlığı’na bağlı olarak kurulmuş bir genel müdürlük. Arsa Ofisi’nin görevi, arsa spekülasyonunu önlemek, bunun için düzenleyici alım satımlar yanında, konut projeleri, sanayi bölgeleri, turistik kuruluşlar ve kamu hizmetleri için gerekli kamulaştırmaları yapmak olarak kabul edilmişti. Daha sonra 2004 yılında çıkarılan 5273 sayılı yasayla Arsa Ofisi Genel Müdürlüğü, teşkilatı ile birlikte kaldırılırken hak ve yükümlülükleri Toplu Konut İdaresi Başkanlığı’na (TOKİ) devredilmiştir.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
