INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10400, 1, "asansör", "İnsanları ya da yükleri yüksek yerlere dikine veya eğik raylar arasında çıkarıp indiren makineli kabin. Bkz. paternoster, montşarj. İlk asansörü Elisha Graves Otis, yük taşımak üzere 1853’te yapmıştır. İlk insan asansörünü de yine Otis 1857 yılında New York’ta bir büyük mağazaya kurmuştur. Avrupa’da ise ilk kez 1867 Paris Uluslararası Sergisi’nde Fransız mühendisi Edoux’nun yaptığı hidrolik kaldırıcı kullanılmıştır. Daha sonra 1889’da Eyfel kulesine kurulmuş olan asansör tepeye yedi dakikada çıkabiliyor ve saatte 2350 kişi taşıyabiliyordu.", "Fr. <strong>(ascenseur)</strong><strong> </strong>", NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
