INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10400, NULL, "hasta asansörü", " Hastanelerde, hastaların sedye ile taşındıkları geniş asansör; hasta asansörü kabini ölçüsü yaklaşık olarak 1,75x2,75 m’dir. ", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10400, NULL, "hidrolik asansör", " Bir motopomp ve piston düzeninden oluşan ve hidrolik basınçla çalışan asansör. Piston, kabinin altında yer alır. Eskiden hidrolik ortam olarak basınçlı sudan yararlanılırdı; bugün su yerine yağ kullanılmaktadır.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10400, NULL, "insan asansörü", " Yalnızca insanların taşınmasında kullanılan asansör.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10400, NULL, "inşaat asansörü", " Yapım sırasında üst katlara malzeme taşımakta kullanılan basit makineli düzenek.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10400, NULL, "merdiven asansörü", " Engelli kişilerin kullanması için merdiven kenarında kurulan ve korkuluğu izleyerek inip çıkan bir oturma yerini ya da bir tekerlekli iskemleyi taşıyacak şekilde platformlu düzenek.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10400, NULL, "oto asansörü", " Rampa yapılması olanağı bulunmayan durumlarda otomobilleri farklı katlara ulaştırmakta kullanılan asansör.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10400, NULL, "panoramik asansör", " Çıkıp inerken bir manzaranın ya da bir iç mekânın görülebilmesi için yapılan, kabini ve çevresi saydam camlı asansör.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10400, NULL, "servis asansörü", " Otel vb. yerlerde, yalnızca çalışanların çeşitli hizmetler için kullandıkları asansör.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10400, NULL, "yük asansörü", " Binalarda yük ve eşya taşımakta kullanılan asansör.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_usages (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10400, NULL, "asansör boşluğu, asansör kuyusu", "Asansörün ve karşı ağırlığın, içinde inip çıktıkları boşluk. ", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_usages (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10400, NULL, "asansör çukuru", "Bir asansör kuyusunun dibinde güvenlik düzeni için bırakılan boşluk<strong>.</strong>", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_usages (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10400, NULL, "asansör kabini ", "Bir asansör sisteminde, taşınacak insan ya da yükün yer aldığı hücre.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_usages (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10400, NULL, "asansör kulesi", " Asansör boşluğunun üzerinde çatıyı aşacak şekilde yükselen, asansör makinesinin yer aldığı bölüm.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_usages (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10400, NULL, "asansör makarası", "Asansör kabininin asılı durduğu kabloları hareket ettiren mekanizmanın tümü.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10401, 1, "asar", " Eserler, yapıtlar.", "Ar. ç. esk. (a’lar uzun okunur)", NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10402, 1, "asarıatika", " Eski eserler, eski yapıtlar. ", "Ar. Far. ç. esk.", NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10403, 1, "asbest,-ti", " Lifli, az çok yumuşak ve ateşte niteliği değişmeyen bir mineral silikat olup tremolitin bozulmasından meydana gelir ve kayalıklarda damarlar halinde bulunur. İki çeşidi vardır. Birincisi esnektir, kolayca eğilip bükülebilen tellerden meydana gelir; birkaç santimetrelik tellere ve ince parçacıklara ayrılabilir; 1500 °C’de ergir. İkincisi kısa tellidir, daha az ayrılabilir; asitlere karşı daha dayanıklıdır, 1100-1300°C’de ergir. Asbest, lifler halinde ayrılabilen başlıca mineraldir. Solunum yoluyla kanser yapıcı etkisi nedeniyle yapıdaki kullanımı azalmış, birçok ülkede de yasaklanmıştır. Bkz. amyant, asbestoz.", "Yun. jeol.", NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_usages (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10403, NULL, "asbest-çimento", " Çatı ve duvar kaplama levhaları ve boru yapmakta kullanılan, asbest lifleri ile karıştırılmış çimento hamuru<em>.</em> Asbestoz nedeniyle üretimi birçok ülkede yasaklanmıştır.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_usages (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10403, NULL, "asbest-çimento levha", " Asbest-çimentodan yapılmış olup çatıların ve duvarların kaplanmasında kullanılan oluklu ya da düz levha. Bkz. eternit.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10404, 1, "asbestoz", "Asbest liflerinin ya da tozlarının sürekli olarak solunmasıyla oluşan akciğer hastalığı. ", "Fr. <strong>(asbestose)</strong><strong> </strong>", NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10405, 1, "asetilen", " Renksiz, sarmısak kokulu, kuvvetli ve beyaz ışık vererek yanan hidrokarbonlu bir gaz (C<sub>2</sub>H<sub>2</sub>). Asetilenden aydınlatma ve kaynak işlerinde yararlanılır. ", "Fr. <strong>(acétylène) </strong>kim.", NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
