INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10407, 1, "asfalt,-tı", NULL, "Yun. ", NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_definitions (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10407, NULL, NULL, "Petrolün ayrışması ile oluşan, içinde bitümlü maddeler bulunan siyah renkte şekilsiz mineral hidrokarbonlara verilen ad. Asfalt, ham petrolü damıtmak yoluyla da elde edilebilir. Asfalt iki türlü uygulanarak su geçirmez bir yüzey sağlanabilir: mastik asfalt ya da silindirlenmiş asfalt olarak. Birbirlerine pek benzeyen bitüm ile asfalt kimyasal bakımdan farklıdırlar. ", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_definitions (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10407, NULL, NULL, "<em>sıf. </em>Asfaltlanmış: <em>asfalt yol.</em>", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10407, NULL, "doğal asfalt", " Topraktan çıkarıldığı durumdaki asfalt.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10407, NULL, "ekmek asfalt", " Doğal asfalta %14-20 bitüm katıp ısıttıktan sonra kalıplara dökerek elde edilen 25 kg’lık blok ya da yapay olarak, ince kalker ununu petrol asıllı bitümle karıştırıp ısıtarak yapılan asfalt.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10407, NULL, "mastik asfalt", " <em>Fr. </em><em><strong>(mastic d’asphalte) </strong></em>Sızdırmazlık için kullanılan mineral agrega, mineral dolgu maddeleri ve asfalt çimentosu karışımından meydana gelen gereç. Sıcak sıvı mastik asfalt, beton dam üzerine ya da bir yol yüzeyine serilip sıkıştırılarak kaplama yapılır. Buna bazen temiz kum ve talaş da karıştırılır. Mastik asfalt genellikle iki ya da üç kat halinde kaplanır.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_derivatives (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10407, NULL, "petrol asfaltı", " Ham petrolün damıtılmasıyla elde edilen, bileşim ve özellikleri bakımından doğal asfaltı çok andıran petrol zifti.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_usages (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10407, NULL, "asfalt dam", " İki üç kat bitümlü keçe ya da mastik asfaltla kaplanmış dam.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_usages (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10407, NULL, "asfalt emülsiyonu", " Dam örtülerinde kullanılmak üzere, asfalt çimentosu ile yapılan asbestli ya da mineral dolgu maddeli bir emülsiyon.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_usages (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10407, NULL, "asfalt makadam", " Bağlayıcı olarak katran yerine asfalt kullanılan makadam türü.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_usages (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10407, NULL, "asfalt taşı", " İçindeki örgensel olmayan maddelerin oranı büyük olan asfalt.", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
