INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10409, 1, "asfaltlı", " İçinde asfalt bulunan. ", "Yun.T.", NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_usages (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10409, NULL, "asfaltlı keçe", " Çatı örtülerinde sızdırmazlık için kullanılan, hayvan kılı ya da bitkisel maddelerle yapılmış ve asfaltla doyurulmuş keçe. Asfaltlı keçe yine asfalt ile yapıştırılır. ", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
