INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10423, 1, "asklepiyon", " Eski Yunan ve Roma’da tıp tanrısı Asklepios adına yapılmış olup çeşitli tedavi kurumlarını da içeren tapınak, <em>°eskülap tapınağı.</em> Bu yapılar hastane işlerini de görmekteydi: Bergama asklepiyonu bunların en ünlülerinden biridir.", "Yun.", NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
