INSERT INTO entry_definitions (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10507, NULL, NULL, "Eski Roma evlerinin ortasında, evin bütün bölümlerinin açıldığı üstü açık, çevresi revaklı avlu. Atriyum’un kökeni konusunda çeşitli varsayımlar ileri sürülmektedir. Bunların içinde, tepesinde dumanın çıkması için bir delik bulunan tek mekânlı ilkel evin geliştirilmiş biçimi varsayımı ağırlık kazanmaktadır. Roma evlerinde de atriyum, evin odağını oluşturan ana hacimdi ve ışığını, çatının ortasındaki kare bir açıklıktan alırdı. Daha sonra atriyum’un kenarlarına odalar açılmış, ancak atriyum aile yaşamının merkezi olarak ortadaki yerini her zaman korumuştur. Çatının eğimi genellikle yağmur sularının atriyumun ortasındaki kare ya da dikdörtgen havuza (impluvium) akıtacak biçimde düzenlenmiştir. Atriyum, üstünü örten çatı biçimlerinin farklılığına göre çeşitli adlar alır. Çatı kirişlerinin mekânı serbestçe geçmeleri durumunda <strong>Atrium Toscanicum,</strong> ışık açıklığını çevreleyen kirişlerin dört sütun tarafından taşınması durumunda da <strong>Atrium Tetrastylicum</strong><em><strong> </strong></em>oluşur. ", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
INSERT INTO entry_definitions (entry_id, type, entry, definition, synonym, created_at, updated_at) VALUES (10507, NULL, NULL, "Kimi Roma tapınaklarında çevresi revaklı, kare biçiminde avlu. ", NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());
