INSERT INTO entries (id, user_id, entry, definition, origin, synonym, see_also, created_at, updated_at) VALUES (10565, 1, "Aztek Mimarlığı ", "Orta Amerika yerlileri Azteklerin, 1100-1520 yılları arasında yani İspanyol fethine değin bugünkü Meksika topraklarında geliştirdikleri mimarlık. Aztekler o yörede daha önce yaşamış olan Tolteklerin mimari biçimlerinden esinlenmiş ve anıtsal ölçekli sade biçimler, ızgara planlar, çoğunlukla insan, çiçek ve tanrı kabartmalarıyla süslenmiş parlak renkli dış cephelerden oluşan bir mimarlık yaratmışlardır. En önemli anıtları olan piramitlerin tepesinde, yamaçlardaki paralel merdivenli yollarla ulaşılan iki tapınak söz konusudur. İspanyol istilası sonrasında Aztek mimarlığından geriye çok az şey kalabilmiş, Azteklerin başkenti de modern Mexico City’nin altında gömülü kalmıştır. Aztek ve Maya mimarlıkları 1920-30’larda Avrupa’daki Art Deco akımına esin kaynağı olmuştur. Bkz. Maya Mimarlığı.", NULL, NULL, NULL, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP());